    function loadHomePagePoll(e, pkid) {
        if (checkCookiePoll(pkid)) {
            isVoted();
            return 1;
        }
        $('.poll_click').click(function (e) {
            if (checkCookiePoll(pollCookieID)) {
                isVoted();
                return 1;
            } else {
                e.preventDefault();
                e.stopPropagation();
                var str = $(this).find("input").attr('id');
                var arr = str.split('_');
                var pid = arr[0];
                var oid = arr[1];
                $(this).find("input").prop("checked", true);
                $.post(postUrl, 'pid=' + pid + '&oid=' + oid, function (response, status) {
                    if (response.code) {
                        let {options, total_vote} = response;
                        let votes = calculateVote(options);
                        $(".progress-bar").css('display', 'block');
                        $(".yourvote").prop("disabled", true);
                        showOptions(votes, total_vote);
                        setCookiePoll(pollCookieID, str, 30);
                        pollSubmitted = true;
                    }
                });
            }
        });
    }
    function checkCookiePoll(cookieName) {
        var user = getCookiePoll(cookieName);
        if (user != "") {
            return 1
        } else {
            return 0;
        }
    }
    function getCookiePoll(cname) {
        var name = cname + "=";
        var decodedCookie = decodeURIComponent(document.cookie);
        var ca = decodedCookie.split(';');
        for (var i = 0; i < ca.length; i++) {
            var c = ca[i];
            while (c.charAt(0) == ' ') {
                c = c.substring(1);
            }
            if (c.indexOf(name) == 0) {
                return c.substring(name.length, c.length);
            }
        }
        return "";
    }
    function setCookiePoll(cname, cvalue, exdays) {
        var d = new Date();
        d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
        var expires = "expires=" + d.toGMTString();
        document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
    }
    function calculateVote(options) {
        let total = 0;
        for (key in options) {
            total += options[key].o_count;
        }
        var opt = [];
        for (key in options) {
            if (total == 0) {
                total = 1;
            }
            opt[options[key].o_id] = Math.round((options[key].o_count / total) * 100);
        }
        return opt;
    }
    function showOptions(votes, totalVote) {
        for (option_id in votes) {
            vote = votes[option_id];
            $("#progress_bar_" + option_id).attr('data-vote', vote);
            $("#option_progress_" + option_id).css('width', vote + "%");
        }
        $(".voteCount").html(totalVote + " Votes");
    }
    function isVoted() {
        if(!pollSubmitted) {
            $(".progress-bar").css('display', 'block');
            $("#already_voted").css('display', 'block');
            $("#al_vote").html("आप वोट कर चुके हैं");
            $(".poll-row").attr("disabled", "disabled");
            $("#" + getCookiePoll(pollCookieID)).prop("checked", true);
            $(".yourvote").prop("disabled", true);
            let votes = calculateVote(optionContent);
            showOptions(votes, totalVote);
        }
    }
}


